import Vuex from "vuex";
import createPersistedDtate from "vuex-persistedstate";

const initialState = {
  user: null,
  token: "",
};

const dataState = createPersistedDtate({
  paths: ["auth.token"],
});

const store = new Vuex.Store({
  modules: {
    auth: initialState,
  },
  plugins: [dataState],
});

export default store;
